import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import React, { Component } from "react";
import CardMedia from "@material-ui/core/CardMedia";
import { CardContent, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import StarIcon from "@material-ui/icons/Star";
import Image1 from "../1.jpeg";

const useStyles = makeStyles(theme => ({
  card: {
    marginLeft: 30
  },
  media: {
    height: 140
  },
  staricon: {
    width: "15px",
    verticalAlign: "middle",
    textAlign: "center"
  },
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    margin: "auto",
    maxWidth: 500
  },
  image: {
    width: 128,
    height: 128
  },
  img: {
    margin: "auto",
    display: "block",
    maxWidth: "100%",
    maxHeight: "100%"
  }
}));
export default function Popular() {
  const classes = useStyles();
  return (
    <div>
      <Container maxWidth="lg" style={{ marginTop: "30px" }}>
        <Grid container spacing={2}>
          <Grid item md={8} lg={8} xs={12} style={{ flexDirection: "row" }}>
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                A Pride Story: Choosing to Live My Black Fat Queer Life
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                As a child, queering my life gave me choices. The main choice?
                Life over death.
              </Typography>
              <Typography
                variant="body2"
                color="black"
                component="p"
                style={{
                  marginLeft: "0px",
                  marginTop: "20px",
                  fontSize: "14px"
                }}
              >
                Cody Charles in Level
              </Typography>
              <Typography
                color="black"
                component="p"
                style={{
                  marginLeft: "0px",
                  marginTop: "10px",
                  fontSize: "12px"
                }}
              >
                Jun 14 2018 &middot; 4 min read
                <StarIcon className={classes.staricon} />
              </Typography>
              <CardMedia
                alt="img1"
                component="img"
                image={Image1}
                title="Image 1"
                height="70"
              />
            </CardContent>
          </Grid>
          <Grid></Grid>
        </Grid>
      </Container>
    </div>
  );
}
