import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import StarIcon from "@material-ui/icons/Star";

const useStyles = makeStyles({
  card: {
    maxWidth: "30%",
    marginLeft: 30
  },
  media: {
    height: 140
  },
  staricon: {
    width: "10px",
    verticalAlign: "middle",
    textAlign: "center"
  }
});

export default function MediaCard() {
  const classes = useStyles();

  return (
    <Card className={classes.card} style={{ boxShadow: "none" }}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image="/static/images/cards/contemplative-reptile.jpg"
          title="Contemplative Reptile"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            A Pride Story: Choosing to Live My Black Fat Queer Life
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            As a child, queering my life gave me choices. The main choice? Life
            over death.
          </Typography>
        </CardContent>
      </CardActionArea>
      <Typography
        variant="body2"
        color="black"
        component="p"
        style={{
          marginLeft: "10px"
        }}
      >
        Cody Charles in Level
      </Typography>
      <Typography
        color="black"
        component="p"
        style={{
          marginLeft: "10px"
        }}
      >
        Jun 14 2018 &middot; <StarIcon className={classes.staricon} />
      </Typography>
      {/* <CardActions
        style={{
          display: "block",
          alignContent: "center",
          justifyContent: "center"
        }}
      >
        <Typography variant="body2" color="black" component="p">
          Cody Charles in Level
        </Typography>
      </CardActions>
      <CardActions>
        <Typography
          variant="body2"
          color="black"
          component="p"
          style={{ display: "block" }}
        >
          Jun 14 2018 &middot;
        </Typography>
      </CardActions> */}
    </Card>
  );
}
