import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import React, { Component } from "react";
import CardMedia from "@material-ui/core/CardMedia";
import { CardContent, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import StarIcon from "@material-ui/icons/Star";
import Image1 from "../1.jpeg";

const useStyles = makeStyles(theme => ({
  card: {
    marginLeft: 30
  },
  media: {
    height: 140
  },
  staricon: {
    width: "15px",
    verticalAlign: "middle",
    textAlign: "center"
  },
  root: {
    flexGrow: 1
  },
  paper: {
    padding: theme.spacing(2),
    margin: "auto",
    maxWidth: 500
  },
  image: {
    width: 128,
    height: 128
  },
  img: {
    margin: "auto",
    display: "block",
    maxWidth: "100%",
    maxHeight: "100%"
  }
}));

export default function Post() {
  const classes = useStyles();
  return (
    <div>
      <Container maxWidth="lg" style={{ marginTop: "30px" }}>
        <Grid container spacing={3}>
          <Grid item md={4} xs={12}>
            <CardMedia
              alt="img1"
              component="img"
              image={Image1}
              title="Image 1"
              height="200"
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                A Pride Story: Choosing to Live My Black Fat Queer Life
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                As a child, queering my life gave me choices. The main choice?
                Life over death.
              </Typography>
              <Typography
                variant="body2"
                color="black"
                component="p"
                style={{
                  marginLeft: "10px",
                  marginTop: "10px"
                }}
              >
                Cody Charles in Level
              </Typography>
              <Typography
                color="black"
                component="p"
                style={{
                  marginLeft: "10px",
                  marginTop: "10px"
                }}
              >
                Jun 14 2018 &middot; <StarIcon className={classes.staricon} />
              </Typography>
            </CardContent>
          </Grid>
          <Grid item md={4} xs={12} xs style={{ paddingLeft: "10px" }}>
            <Grid style={{ flexDirection: "row", display: "flex" }}>
              <CardMedia
                style={{ width: 150 }}
                alt="img1"
                component="img"
                image={Image1}
                title="Image 1"
                height="150"
              />
              <CardContent>
                <Typography
                  gutterBottom
                  variant="h5"
                  component="h2"
                  style={{ fontSize: "14px" }}
                >
                  A Pride Story: Choosing to Live My Black Fat Queer Life
                </Typography>
                <Typography
                  variant="body2"
                  color="black"
                  component="p"
                  style={{
                    marginLeft: "10px",
                    marginTop: "10px"
                  }}
                >
                  Cody Charles in Level
                </Typography>
                <Typography
                  color="black"
                  component="p"
                  style={{
                    marginLeft: "10px",
                    marginTop: "10px"
                  }}
                >
                  Jun 14 2018 &middot; <StarIcon className={classes.staricon} />
                </Typography>
              </CardContent>
            </Grid>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}
